import gsap from "gsap";

const prevArrow = document.querySelector(".PrArrow");
const nextArrow = document.querySelector(".NeArrow");

prevArrow.addEventListener("mouseover", () => {
  gsap.to(".PrArrow", {
    x: -5,
  });

})

nextArrow.addEventListener("mouseover", () => {
  gsap.to(".NeArrow", {
    x: 5,
  });
})
