  }

  return (
    <>
      <section id="pals">
        <div>
          <div 
            className="arrowsNav"
            onClick={() => scrollMsg("Prev")}
          >
            <picture>&lt;</picture>
          </div>

          <div 
            className="arrowsNav"
            onClick={() => scrollMsg("Next")}
          >
            <picture>&gt;</picture>
          </div>

          <div 
            className="msg"
            style={{
              left: (position + "%")
            }}
          >
            {
              palsInfo.map(({ author, quote, ceo }) => {
                return (
                  <div 
                    className="palsInfo"
                    key={Math.floor(Math.random()) * 10}
                  >
                    <h1>OUR CLIENTS APPROVE</h1>
                    <p>
                      {quote}
                    </p>
                    <h2>
                      {author} <br />
                      <span>CEO OF {ceo}</span>
                    </h2>
                  </div>
                );
              })
            }
          </div>
        </div>

        <div className="palsComp"></div>
      </section>
    </>
  );
}

export default Pals;
