// Hooks Import
import { useState } from "react";

// Images
import arrowPrev from "../../../icons/back-arrow.svg";
import arrowNext from "../../../icons/front-arrow.svg";

function Top() {
  const [position, setPosition] = useState(0);
  const [profColor, setProfColor] = useState("#EF6F6C");
  const [profShape, setProfShape] = useState("50%");
  const [prevArrOpacity, setPrevArrOpacity] = useState("50%");
  const [nextArrOpacity, setNextArrOpacity] = useState("50%");

  const changeProf = (Arr) => {
    if (Arr === "Prev" && position > -300) {

    }

    if (Arr === "Next" && position < 0) {

    }
  }

  const arrOpacity = () => {
    if (position === 0) {
      setPrevArrOpacity(0.3);
    } else {
      setPrevArrOpacity(1);
    }

    if (position === -300) {
      setNextArrOpacity(0.3);
    } else {
      setNextArrOpacity(1);
    }
  };

  const changeProfStyle = (msg) => {
    switch (msg) {
      case 0:
        setProfColor("#EF6F6C");
        setProfShape("50%");
        return;

      case -100:
        setProfColor("#5AD2F4");
        setProfShape("50%");
        return;

      case -200:
        setProfColor("#7FB685");
        setProfShape("50%");
        return;

      case -300:
        setProfColor("#F9C976");
        setProfShape("50%");
        return;

      default:
        return;
    }
  };

  return (
    <>
      <section id="top">
        <div>
          <div
            className="arrowsNav"
            onClick={() => changeProf("Prev")}
            onLoad={() => arrOpacity()}
            style={{ opacity: prevArrOpacity }}
          >
            <picture>
              <img
                src={arrowPrev}
                alt="Previous Testimonial"
                className="PrArrow"
              />
            </picture>
          </div>

          <div
            className="arrowsNav"
            onClick={() => changeProf("Next")}
            onLoad={() => arrOpacity()}
            style={{ opacity: nextArrOpacity }}
          >
            <picture>
              <img src={arrowNext} alt="Next Testimonial" className="NeArrow" />
            </picture>
          </div>

          <h1 className="bolder center title">
            Be in the top with the greatest
          </h1>

          <div className="profInfo">
            <div className="profDesc">
              <p>
                Lorem ipsum, dolor sit amet consectetur adipisicing elit. Nobis
                sapiente doloremque quia ducimus nostrum officiis aliquid sed
                nesciunt, nisi necessitatibus corrupti non, alias dignissimos
                magnam. Totam impedit magnam voluptatibus perferendis.
              </p>

              <div className="buttonDiv">
                <button></button>

                <button></button>

                <button></button>

                <button></button>
              </div>
            </div>

            <div 
              className="imgProf"
              style={{
                border: `15px solid ${profColor}`
              }}
            ></div>
          </div>
        </div>
      </section>
    </>
  );
}

export default Top;
